import React, { Component } from "react";
import * as bootstrap from "bootstrap"
import * as reactstrap from  "reactstrap"

class Loginresult {
  render() {
    return (
      <div class={"alert" + this.props.mType} role="alert">{this.props.Data}</div>
    )
  }
}

export class Login extends Component {
  constructor(props) {
    super(props);
    this.handleSubmit = this.handleSubmit.bind(this);
    this.state = {data:'sample text', mt:'alert-primary'};
  }
  handleSubmit(event) {
    event.preventDefault();
    let email = event.target.querySelector("#email");
    let password = event.target.querySelector("#password");
  }
  render() {
    return (
     <div class="container">
      <div class="row d-flex justify-content-center">
         <div class="col-8 text-center">
           <form onSubmit={(event)=>this.handleSubmit(event)}>
                <br />
                <br />
                <h3>Log In</h3>
                <br />
                <br />
                <div className="input-group">
                    <div className="input-group-prepend">
                        <label className="input-group-text" htmlFor="email">Email address</label>
                    </div>
                    <input type="email" className="form-control" placeholder="Enter email here" />
                </div>
                <br />
                <div className="input-group">
                    <div className="input-group-prepend">
                       <label className="input-group-text" htmlFor="password">Password</label>
                    </div>
                    <input type="password" className="form-control" placeholder="Enter password here" />
                </div>
                <br />
                <div className="form-group">
                    <div className="custom-control custom-checkbox">
                        <input type="checkbox" className="custom-control-input" id="customCheck1" />
                        <label className="custom-control-label" htmlFor="customCheck1">Remember me</label>
                    </div>
                </div>
                <br />
                <button type="submit" className="btn btn-primary btn-block yolonet-submit">Submit</button>
                <br />
                <p className="forgot-password text-right">
                    Forgot <a href="/passReset">password?</a>
                </p>
            </form>
            <br />
            <br />
            <Loginresult Data={this.state.data} mType={this.state.mt} />
        </div>
      </div>
     </div>
    );
  }
}

export default Login;
